$(function () {
    // Aplicar validação ao formulário de contato
    $("#contactForm input, #contactForm textarea").jqBootstrapValidation({
        preventSubmit: true,
        submitError: function ($form, event, errors) {
            // Caso de erro durante a submissão
        },
        submitSuccess: function ($form, event) {
            event.preventDefault(); // Prevenir submissão padrão do formulário

            // Coletar dados do formulário
            var name = $("input#name").val();
            var email = $("input#email").val();
            var phone = $("input#phone").val();
            var message = $("textarea#message").val();
            var firstName = name;

            // Extrair primeiro nome, caso haja espaço
            if (firstName.indexOf(" ") >= 0) {
                firstName = name.split(" ").slice(0, -1).join(" ");
            }

            // Referência ao botão de envio
            var $sendButton = $("#sendMessageButton");
            $sendButton.prop("disabled", true); // Desabilitar o botão durante a submissão

            // Enviar dados por AJAX
            $.ajax({
                url: "",
                type: "POST",
                data: {
                    name: name,
                    phone: phone,
                    email: email,
                    message: message
                },
                cache: false,
                success: function () {
                    // Mensagem de sucesso
                    $("#success").html("<div class='alert alert-success'>");
                    $("#success > .alert-success")
                        .html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button>")
                        .append("<strong>Sua mensagem foi enviada com sucesso. </strong>")
                        .append("</div>");
                    // Resetar o formulário
                    $("#contactForm").trigger("reset");
                },
                error: function () {
                    // Mensagem de erro
                    $("#success").html("<div class='alert alert-danger'>");
                    $("#success > .alert-danger")
                        .html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button>")
                        .append($("<strong>").text("Desculpe " + firstName + ", parece que o servidor de e-mail não está respondendo. Por favor, tente novamente mais tarde!"))
                        .append("</div>");
                    // Resetar o formulário
                    $("#contactForm").trigger("reset");
                },
                complete: function () {
                    // Reabilitar o botão após 1 segundo
                    setTimeout(function () {
                        $sendButton.prop("disabled", false);
                    }, 1000);
                }
            });
        },
        filter: function () {
            return $(this).is(":visible");
        }
    });

    // Controle para os links de abas
    $('a[data-toggle="tab"]').click(function (event) {
        event.preventDefault();
        $(this).tab("show");
    });
});

// Limpar mensagens de sucesso/erro ao focar no campo de nome
$("#name").focus(function () {
    $("#success").html("");
});